import React, { Component } from 'react';
import './App.css';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';


const styles = theme => ({
  button: {
    margin: theme.spacing.unit,
  },
  input: {
    display: 'none',
  },
});

function onClick(){
  const text = document.querySelector(".textbox").value;
  var myJSON = {"text": text };
  makeRequest(myJSON);
  console.log(myJSON)
}

function makeRequest(myJSON){
  var headers = {
    "Content-Type": "application/json",                                                                                                
    "Access-Control-Origin": "*"
 }
 var data = {
  "email": "peter@klaven",
  "password": "cityslicka"
}

fetch("https://localhost:5000/summary", {
  method: "POST",
  headers: headers,
  body:  JSON.stringify(myJSON)
})
.then(function(response){ 
  console.log(response.json())
  return response.json(); 
})
}



function SubmitButton(props){
  const { classes } = props;
    return (
      <Button onClick={onClick} variant="contained" color="secondary" className={classes.button} id = "submitButton">
        Submit
      </Button>
    );
}

SubmitButton.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SubmitButton);
